mapper.select:
    select 
        
    from  attends
    
	     and AttendUser = #{attendUser,jdbcType=INTEGER} 
	    and StartTime >= #{startTime,jdbcType=TIMESTAMP} 
	    and EndTime <= #{endTime,jdbcType=TIMESTAMP} 
	
	  order by StartTime desc
  	
		limit
		#{offset,jdbcType=INTEGER},#{limitnum,jdbcType=INTEGER}

    select 
        count(AttendId)
    from  attends
     
	     and AttendUser = #{attendUser,jdbcType=INTEGER} 
	    and StartTime >= #{startTime,jdbcType=TIMESTAMP} 
	    and EndTime <= #{endTime,jdbcType=TIMESTAMP}
